CURSOR CURSUPPLY(a_dept IN VARCHAR2, a_htag IN VARCHAR2) IS
SELECT BUILDNO,
       HOUSENO,
       LPAD(CHANGE_BUILDNO, 4, ' '),
       LPAD(CHANGE_HOUSENO, 4, ' '),
       SQUARE,
       TYPE,
       CLASS,
       OPTIONCODE,
       VATTAG,
       EXCLUSIVEAREA,
       COMMONAREA,
       ETCCOMMONAREA,
       PARKINGAREA,
       SERVICEAREA,
       SITEAREA,
       FLOOR,
       GUBUN,
       CATEGORY_NAME,
       CONTRACTYESNO,
       VIRDEPOSIT,
       VIRDEPOSIT2,
       BANK_CODE,
       BANK_NAME,
       USE_YN,
       RENT_TAG,
       PRTSQUARE,
       PREDISAMT
  FROM HD_HOUS_SUPPLY_CHANGE
 WHERE DEPT_CODE = a_dept
   AND HOUSETAG  = a_htag;

/*---------------------------------------------------------------------------*/
/*---------------------------------------------------------------------------*/
BEGIN
