                  RAISE_APPLICATION_ERROR(-20002, 'HD_TAX_CALCULATE_AGREE:12 생성하는중 오류가 발생했습니다.');
                  RAISE_APPLICATION_ERROR(-20002, '9> ' || c_cust || '-' || TO_CHAR(c_seq) || '-' || d_counts);
                  ROLLBACK;
