         END; END IF;
      END; END IF;
      /* ------------------------------------------------------------------------------------*/

   END LOOP;
   CLOSE CURCUST;

   COMMIT WORK;

   EXCEPTION
        WHEN OTHERS THEN
             ROLLBACK;
             raise_application_error( -20001,  sqlcode || ':' || sqlerrm );
             --dbms_output.put_line(as_dept);
END;
