                ELSE
   	            C_R_BUILDAMT  := TRUNC(((C_BUILDAMT / (C_BUNAMT - C_MANAGEAMT)) * (C_R_RECEIPTAMT - C_R_MANAGEAMT)) / 10) * 10;
