      Wk_errflag := 0;                         -- 사용자 정의 Error Code
   ELSE
      Wk_errmsg := RTRIM(e_msg) || '/>';
      RAISE UserErr;
   END IF;
   EXCEPTION
      WHEN UserErr THEN
       RAISE_APPLICATION_ERROR(Wk_errflag, Wk_errmsg);
END proc_hd_rent_gurt_income_plan;
