   /*---------------------------------------------------------------------------*/
   BEGIN
      UPDATE HD_HOUS_SUPPLY
         SET CONTRACTYESNO = 'Y',
             VIRDEPOSIT    = '',
             CHG_DUTY_ID   = as_user,
             CHG_DATE      = v_sysdt
       WHERE DEPT_CODE     = v_dept
