v_sysdt          VARCHAR2(14);
v_cnt            NUMBER(10);
v_sliptp         VARCHAR2(5);

/*---------------------------------------------------------------------------*/
v_taxcomp        VARCHAR2(6);
v_company        VARCHAR2(6);
