BEGIN
   v_cnt := 0;

--   v_cnt := 0;
--   SELECT COUNT(*)
--     INTO v_cnt
--     FROM HD_CODE_DEPT A,
--          TM_ENDX_HIS_DAY B
--    WHERE A.COMPANY_CODE = B.COMPANY_CODE
--      AND A.DEPT_CODE = as_dept
