   SELECT TAX_COMP_CODE
     INTO v_taxcomp
     FROM TM_CODE_DEPT
    WHERE COMPANY_CODE = v_company
      AND DEPT_CODE    = as_dept;

   v_sysdt := TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS');
   v_remark  := v_deptnm || ' ' || v_htagnm;
   v_WorkSeq := 0;
   v_slipamt := 0;
   v_sliptp  := 'HDA';

   /**** ----------------------------------------------------------------------------------------------- ****/
   /**** ----------------------------------------------------------------------------------------------- ****/
   /* HDA 납입 전표 */   /* 보증금 */
