            VALUES ( v_company,      as_dt,         v_WorkNo,        v_WorkSeq,      v_WorkSeq,
                     v_WorkNo,       as_dept,       v_DeptNo,        v_sliptp,       '002',
                     NULL,           NULL,          NULL,            NULL,           z_1d,
