            VALUES ( v_company,       as_dt,          v_WorkNo,    v_WorkNo,      as_dept,
                     v_DeptNo,        v_sliptp,       '002',       '1',           as_user,
                     v_sysdt,         NULL,           NULL,        NULL,          NULL,
                     NULL,            NULL,           v_remarkH,   c_amt,         NULL,
                     NULL,            'Y' );
