        (B.CHANGETAG <> '1' AND B.CHANGEDATE    > a_dt))
   AND B.LASTCHANGEDATE  <= a_dt

   AND A.RECEIPTDATE      < D.ORG_AGREEDATE

   AND A.PAYTAG           NOT IN ('E', 'C', 'F')
