    SELECT NVL(SUM(A.RECEIPTAMT), 0)
      INTO v_slipamt
      FROM HD_HOUS_INCOME A,
           VW_HD_HOUS_SELL B,
           HD_CODE_DEPOSIT C
     WHERE A.CUST_CODE        = B.CUST_CODE
