       AND A.HOUSETAG         = C.HOUSETAG
       AND A.DEPOSIT_NO       = C.DEPOSIT_NO
--       AND NVL(A.SLIPSEQ, 0)  = 0
       AND A.DEPT_CODE        = as_dept
