                    INSERT INTO TM_SLIP_DETAIL
                              ( WORK_COMP,      WORK_DATE,     WORK_NO,         WORK_SEQ,       WORK_SEQ_SCR,
                                WORK_NO_GROUP,  WORK_DEPT,     WORK_DEPT_NO,    SLIP_KIND_CODE, SLIP_DIV,
