                                COMPANY_CODE,   DEPT_CODE,     PROJ_CODE,       DR_AMT,         DR_CASH,
                                DR_VAT,         DR_SUPPLY,     CR_AMT,          CR_CASH,        CR_VAT,
                                CR_SUPPLY,      REMARK1,       REMARK2,         TAX_COMP_CODE,  CUST_CODE,
