                  RETURN;
       END;

       OPEN CURZM6(as_dept, as_htag, as_dt);
       LOOP
          FETCH CURZM6
           INTO zs_deposit, zs_deposithd, zs_bank,  zs_banknm,
                zs_amt,     zs_lamt,      zs_bamt1, zs_bamt2,
