                 AND A.RECEIPTDATE      = as_dt
                 AND A.COUNTS          <> '99'
                 AND ((A.COUNTS <> '90') OR (A.COUNTS = '90' AND A.TIMES < 90))
                 
                 AND A.RECEIPTDATE      > D.AGREEDATE

                 AND A.PAYTAG           NOT IN ('E', 'C', 'F')
                 AND A.INCOMTYPE        IN ('1', '4', '6', '7', '8')
                 AND C.OUTDEPOSITNO     = h2_deposit
