                            DR_VAT,              DR_SUPPLY,     CR_AMT,              CR_CASH,        CR_VAT,
                            CR_SUPPLY,           REMARK1,       REMARK2,             TAX_COMP_CODE,  CUST_CODE,
                            CUST_NAME,           EVIDENCE_CODE, EVIDENCE_DATE,       EVIDENCE_KIND,  EVIDENCE_NO,
                            SALES_DIV,           SLIP_SEQ,      BANK_CODE,           DEPOSIT_NO,     CHECK_NO,
