                                as_user,        v_sysdt,       NULL,            NULL);
                    EXCEPTION
                       WHEN OTHERS THEN
                            dbms_output.put_line(sqlerrm);
                            RAISE_APPLICATION_ERROR(-20002, '6-5(분양대금선공제분): TM_SLIP_DETAIL INSERT중 오류가 발생했습니다');
                            ROLLBACK;
                            RETURN;
                 END;
              END; END IF;

              /* -- 대변 분양수입선수금(부가세) -- */
              IF he_vamt <> 0 THEN BEGIN
