             INSERT INTO TM_SLIP_MASTER
                       ( WORK_COMP,       WORK_DATE,      WORK_NO,     WORK_NO_GROUP, WORK_DEPT,
                         WORK_DEPT_NO,    SLIP_KIND_CODE, SLIP_DIV,    CHECK_STATUS,  INPUT_DUTY_ID,
