                     v_dt,           NULL,          NULL,            NULL,           NULL,
                     NULL,           NULL,          NULL,            NULL,           DECODE(s_yn, 'N', s_4c, s_tc),
                     as_user,        v_sysdt,       NULL,            NULL);
         EXCEPTION
            WHEN OTHERS THEN
                 dbms_output.put_line(sqlerrm); 
                 RAISE_APPLICATION_ERROR(-20002, '3: TM_SLIP_DETAIL INSERT중 오류가 발생했습니다');
                 ROLLBACK;
                 RETURN;
